import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import AssessmentDashboard from '../components/AssessmentDashboard';
import AssessmentQuestion from '../components/AssessmentQuestion';
import AssessmentResult from '../components/AssessmentResult';
import Timer from '../components/Timer';
import AdminAssessmentManager from '../components/AdminAssessmentManager';
import { ASSESSMENT_TYPES, TIMER_TYPES, createAssessment, createQuestion } from '../utils/types';
import { toast } from 'react-hot-toast';

const SelfAssessmentPage = () => {
  const { user } = useAuth();
  const [currentView, setCurrentView] = useState('dashboard'); // 'dashboard', 'assessment', 'result', 'admin'
  const [currentAssessment, setCurrentAssessment] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [assessmentHistory, setAssessmentHistory] = useState([]);
  const [score, setScore] = useState(null);
  const [availableAssessments, setAvailableAssessments] = useState([]);

  // Mock data for demonstration
  useEffect(() => {
    // In a real app, this would fetch from your API
    setAssessmentHistory([
      {
        id: '1',
        title: 'JavaScript Fundamentals',
        score: 85,
        completedAt: '2024-03-15T10:30:00Z'
      },
      {
        id: '2',
        title: 'React Basics',
        score: 92,
        completedAt: '2024-03-14T15:45:00Z'
      }
    ]);

    // Also fetch available assessments
    setAvailableAssessments([
      createAssessment({
        id: '1',
        title: 'JavaScript Fundamentals',
        description: 'Test your knowledge of JavaScript basics',
        type: ASSESSMENT_TYPES.POINT_BASED,
        timerType: TIMER_TYPES.FULL_TEST,
        totalTime: 30, // 30 minutes
        passingScore: 70,
        questions: [
          createQuestion({
            id: 'q1',
            text: 'What is the correct way to declare a variable in JavaScript?',
            type: ASSESSMENT_TYPES.POINT_BASED,
            options: [
              'var x = 5;',
              'const x = 5;',
              'variable x = 5;',
              'x = 5;'
            ],
            points: 10,
            correctAnswer: 1 // index of 'const x = 5;'
          }),
          createQuestion({
            id: 'q2',
            text: 'Which of the following is NOT a JavaScript data type?',
            type: ASSESSMENT_TYPES.POINT_BASED,
            options: [
              'String',
              'Boolean',
              'Character',
              'Number'
            ],
            points: 10,
            correctAnswer: 2 // index of 'Character'
          }),
          createQuestion({
            id: 'q3',
            text: 'What does HTML stand for?',
            type: ASSESSMENT_TYPES.POINT_BASED,
            options: [
              'Hyper Text Markup Language',
              'High-level Text Management Language',
              'Hyperlink and Text Markup Language',
              'Home Tool Markup Language'
            ],
            points: 10,
            correctAnswer: 0 // index of 'Hyper Text Markup Language'
          }),
        ]
      }),
      createAssessment({
        id: '2',
        title: 'React Basics',
        description: 'Test your understanding of React fundamentals',
        type: ASSESSMENT_TYPES.POINT_BASED,
        timerType: TIMER_TYPES.PER_QUESTION,
        totalTime: 60, // 60 seconds per question
        passingScore: 70,
        questions: [
          createQuestion({
            id: 'rq1',
            text: 'What is the primary function of React?',
            type: ASSESSMENT_TYPES.POINT_BASED,
            options: [
              'To manage databases',
              'To build user interfaces',
              'To handle server-side logic',
              'To style web pages'
            ],
            points: 10,
            correctAnswer: 1 // index of 'To build user interfaces'
          }),
          createQuestion({
            id: 'rq2',
            text: 'Which of the following is used to pass data to a Component from outside?',
            type: ASSESSMENT_TYPES.POINT_BASED,
            options: [
              'setState',
              'render with arguments',
              'props',
              'PropTypes'
            ],
            points: 10,
            correctAnswer: 2 // index of 'props'
          }),
        ]
      }),
    ]);
  }, []);

  const handleStartAssessment = (assessment) => {
    setCurrentAssessment(assessment);
    setCurrentQuestionIndex(0);
    setAnswers({});
    setCurrentView('assessment');
  };

  const handleAnswer = (questionId, answerIndex) => {
    setAnswers(prev => ({
      ...prev,
      [questionId]: answerIndex
    }));
  };

  const handleNextQuestion = () => {
    const currentQuestion = currentAssessment.questions[currentQuestionIndex];
    
    // Forced Responses: Check if an answer is selected for the current question
    if (answers[currentQuestion.id] === undefined || answers[currentQuestion.id] === null) {
      toast.error('Please answer the current question before proceeding.');
      return;
    }

    // Adaptive Assessments: Check for branch logic
    if (currentQuestion.branchLogic) {
      const selectedAnswerIndex = answers[currentQuestion.id];
      const nextQuestionId = currentQuestion.branchLogic[selectedAnswerIndex];

      if (nextQuestionId) {
        const nextQuestionIndex = currentAssessment.questions.findIndex(q => q.id === nextQuestionId);
        if (nextQuestionIndex !== -1) {
          setCurrentQuestionIndex(nextQuestionIndex);
          return;
        }
      }
    }

    // Default: Move to the next sequential question
    if (currentQuestionIndex < currentAssessment.questions.length - 1) {
      setCurrentQuestionIndex(prev => prev + 1);
    } else {
      handleSubmitAssessment();
    }
  };

  const handlePreviousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(prev => prev - 1);
    }
  };

  const handleSubmitAssessment = () => {
    // Calculate score
    const totalQuestions = currentAssessment.questions.length;
    const correctAnswers = Object.entries(answers).filter(([questionId, answerIndex]) => {
      const question = currentAssessment.questions.find(q => q.id === questionId);
      return question.correctAnswer === answerIndex;
    }).length;

    const calculatedScore = Math.round((correctAnswers / totalQuestions) * 100);
    setScore(calculatedScore);

    // Add to history
    const newHistory = {
      id: currentAssessment.id,
      title: currentAssessment.title,
      score: calculatedScore,
      completedAt: new Date().toISOString()
    };
    setAssessmentHistory(prev => [newHistory, ...prev]);

    setCurrentView('result');
  };

  const handleTimeUp = () => {
    if (currentAssessment.timerType === TIMER_TYPES.PER_QUESTION) {
      handleNextQuestion();
    } else {
      handleSubmitAssessment();
    }
  };

  // Admin functions
  const handleSaveAssessment = (assessmentToSave) => {
    setAvailableAssessments(prevAssessments => {
      const existingIndex = prevAssessments.findIndex(a => a.id === assessmentToSave.id);
      if (existingIndex !== -1) {
        // Update existing assessment
        const updatedAssessments = [...prevAssessments];
        updatedAssessments[existingIndex] = assessmentToSave;
        toast.success('Assessment updated successfully!');
        return updatedAssessments;
      } else {
        // Add new assessment
        toast.success('Assessment created successfully!');
        return [...prevAssessments, assessmentToSave];
      }
    });
    setCurrentView('dashboard'); // Go back to dashboard after saving
  };

  const handleDeleteAssessment = (assessmentId) => {
    setAvailableAssessments(prevAssessments => {
      toast.success('Assessment deleted successfully!');
      return prevAssessments.filter(a => a.id !== assessmentId);
    });
  };

  const renderContent = () => {
    switch (currentView) {
      case 'dashboard':
        return (
          <div className="space-y-6">
            <div className="flex justify-between items-center">
              <h1 className="text-2xl font-bold text-gray-900">Self-Assessments</h1>
              {user?.role === 'admin' && (
                <button
                  onClick={() => setCurrentView('admin')}
                  className="btn-primary"
                  type="button"
                >
                  Manage Assessments
                </button>
              )}
            </div>
            <AssessmentDashboard assessmentHistory={assessmentHistory} />

            {/* Available Assessments */}
            <div className="bg-white shadow rounded-lg p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Available Assessments</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {availableAssessments.map((assessment) => (
                  <div key={assessment.id} className="border rounded-lg p-6">
                    <h3 className="text-lg font-medium text-gray-900 mb-2">{assessment.title}</h3>
                    <p className="text-gray-600 mb-4">{assessment.description}</p>
                    <div className="flex flex-wrap gap-2 mb-4">
                      <span className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm">
                        {assessment.questions.length} Questions
                      </span>
                      <span className="px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm">
                        {assessment.totalTime} {assessment.timerType === TIMER_TYPES.PER_QUESTION ? 'min per question' : 'min total'}
                      </span>
                    </div>
                    <button
                      onClick={() => handleStartAssessment(assessment)}
                      className="btn-primary w-full"
                    >
                      Start Assessment
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        );

      case 'assessment':
        const currentQuestion = currentAssessment.questions[currentQuestionIndex];
        return (
          <div className="max-w-3xl mx-auto space-y-6">
            <div className="flex justify-between items-center">
              <h2 className="text-xl font-semibold text-gray-900">
                {currentAssessment.title}
              </h2>
              {currentAssessment.timerType === TIMER_TYPES.FULL_TEST && (
                <Timer
                  initialTime={currentAssessment.totalTime * 60}
                  onTimeUp={handleTimeUp}
                />
              )}
            </div>

            <div className="mb-4">
              <span className="text-sm font-medium text-gray-500">
                Question {currentQuestionIndex + 1} of {currentAssessment.questions.length}
              </span>
            </div>

            <AssessmentQuestion
              question={currentQuestion}
              onAnswer={handleAnswer}
              currentAnswer={answers[currentQuestion.id]}
              timerType={currentAssessment.timerType}
              onTimeUp={handleTimeUp}
            />

            <div className="flex justify-between">
              <button
                onClick={handlePreviousQuestion}
                disabled={currentQuestionIndex === 0}
                className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Previous
              </button>
              <button
                onClick={handleNextQuestion}
                className="btn-primary"
              >
                {currentQuestionIndex === currentAssessment.questions.length - 1
                  ? 'Submit'
                  : 'Next'}
              </button>
            </div>
          </div>
        );

      case 'result':
        return (
          <div className="max-w-3xl mx-auto">
            <AssessmentResult
              assessment={currentAssessment}
              score={score}
              answers={answers}
            />
            <div className="mt-6 flex justify-center">
              <button
                onClick={() => setCurrentView('dashboard')}
                className="btn-primary"
              >
                Back to Dashboard
              </button>
            </div>
          </div>
        );

      case 'admin':
        if (user?.role !== 'admin') {
          return <p className="text-red-500">Access Denied. Admins only.</p>;
        }
        return (
          <div className="space-y-6">
            <div className="flex justify-between items-center mb-4">
              <h1 className="text-2xl font-bold text-gray-900">Manage Self-Assessments</h1>
              <button
                onClick={() => setCurrentView('dashboard')}
                className="btn-secondary"
              >
                Back to Dashboard
              </button>
            </div>
            <AdminAssessmentManager
              assessments={availableAssessments}
              onSave={handleSaveAssessment}
              onDelete={handleDeleteAssessment}
            />
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      {renderContent()}
    </div>
  );
};

export default SelfAssessmentPage;
