import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import logo from './logo.jpg';

const Navbar = () => {
  const { isAuthenticated, logout, user } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <nav className="bg-[#1A1A1A] shadow-lg font-['Montserrat']">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-20">
          <div className="flex items-center">
            <Link to="/" className="flex-shrink-0 flex items-center">
              <img
                className="h-12 w-auto"
                src={logo}
                alt="The Catalystz"
              />
              <span className="ml-3 text-white text-xl font-['Playfair_Display']">The Catalystz</span>
            </Link>
          </div>

          <div className="flex items-center">
            {isAuthenticated ? (
              <div className="flex items-center space-x-4">
                <span className="text-[#E5E5E5] font-['Open_Sans']">
                  Welcome, {user?.name || 'User'}
                </span>
                {user?.role === 'admin' ? (
                  <Link
                    to="/admin"
                    className="text-[#E5E5E5] hover:text-[#FFD700] px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200"
                  >
                    Admin Dashboard
                  </Link>
                ) : (
                  <Link
                    to="/student/dashboard"
                    className="text-[#E5E5E5] hover:text-[#FFD700] px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200"
                  >
                    My Courses
                  </Link>
                )}
                {user?.role === 'admin' && (
                  <Link
                    to="/admin/upload-video"
                    className="text-[#E5E5E5] hover:text-[#FFD700] px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200"
                  >
                    Video Upload
                  </Link>
                )}
                <button
                  onClick={handleLogout}
                  className="bg-[#FFD700] text-[#1A1A1A] px-4 py-2 rounded-md text-sm font-medium hover:bg-[#FFE44D] transition-colors duration-200"
                >
                  Logout
                </button>
              </div>
            ) : (
              <div className="flex items-center space-x-4">
                <Link
                  to="/login"
                  className="text-[#E5E5E5] hover:text-[#FFD700] px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200"
                >
                  Login
                </Link>
                <Link
                  to="/register"
                  className="bg-[#FFD700] text-[#1A1A1A] px-4 py-2 rounded-md text-sm font-medium hover:bg-[#FFE44D] transition-colors duration-200"
                >
                  Register
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
