import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';

const CourseDetails = () => {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // In a real app, this would fetch course details from the server
    // Mock data for demonstration
    setCourse({
      id: id,
      title: 'Web Development Bootcamp',
      description: 'Learn web development from scratch with this comprehensive course covering HTML, CSS, JavaScript, and more.',
      instructor: 'John Smith',
      duration: '12 weeks',
      level: 'Beginner',
      modules: [
        {
          id: 1,
          title: 'Introduction to HTML',
          duration: '2 hours',
          completed: false,
        },
        {
          id: 2,
          title: 'CSS Fundamentals',
          duration: '3 hours',
          completed: false,
        },
        {
          id: 3,
          title: 'JavaScript Basics',
          duration: '4 hours',
          completed: false,
        },
      ],
      thumbnail: 'https://via.placeholder.com/800x400',
    });
    setLoading(false);
  }, [id]);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (!course) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold text-gray-900">Course not found</h2>
        <p className="mt-2 text-gray-600">The course you're looking for doesn't exist.</p>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Course Header */}
      <div className="bg-white shadow rounded-lg overflow-hidden">
        <div className="relative h-64">
          <img
            src={course.thumbnail}
            alt={course.title}
            className="w-full h-full object-cover"
          />
          <div className="absolute inset-0 bg-black bg-opacity-40 flex items-center justify-center">
            <h1 className="text-4xl font-bold text-white">{course.title}</h1>
          </div>
        </div>
        <div className="p-6">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-4">
              <span className="px-3 py-1 text-sm font-medium text-blue-800 bg-blue-100 rounded-full">
                {course.level}
              </span>
              <span className="text-gray-500">{course.duration}</span>
            </div>
            <button className="btn-primary">Enroll Now</button>
          </div>
          <p className="text-gray-600 mb-6">{course.description}</p>
          <div className="flex items-center text-gray-500">
            <svg
              className="h-5 w-5 mr-2"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
              />
            </svg>
            <span>Instructor: {course.instructor}</span>
          </div>
        </div>
      </div>

      {/* Course Modules */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-900">Course Modules</h2>
        </div>
        <div className="divide-y divide-gray-200">
          {course.modules.map((module) => (
            <div key={module.id} className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="text-lg font-medium text-gray-900">
                    {module.title}
                  </h3>
                  <p className="mt-1 text-sm text-gray-500">
                    Duration: {module.duration}
                  </p>
                </div>
                <Link
                  to={`/courses/${id}/module/${module.id}`}
                  className="btn-secondary"
                >
                  {module.completed ? 'Review' : 'Start'}
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Course Requirements */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-900">Requirements</h2>
        </div>
        <div className="p-6">
          <ul className="list-disc list-inside space-y-2 text-gray-600">
            <li>Basic computer skills</li>
            <li>No prior programming experience required</li>
            <li>Dedication to learn and practice</li>
            <li>Computer with internet connection</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default CourseDetails;
