import React from 'react';
import PropTypes from 'prop-types';
import Timer from './Timer';
import { ASSESSMENT_TYPES } from '../utils/types';

const AssessmentQuestion = ({
  question,
  onAnswer,
  currentAnswer,
  timerType,
  onTimeUp
}) => {
  const renderLikertScale = () => (
    <div className="space-y-4">
      {question.options.map((option, index) => (
        <label
          key={index}
          className="flex items-center p-4 border rounded-lg cursor-pointer hover:bg-gray-50 transition-colors duration-200"
        >
          <input
            type="radio"
            name={`question-${question.id}`}
            value={index}
            checked={currentAnswer === index}
            onChange={() => onAnswer(question.id, index)}
            className="h-4 w-4 text-blue-600 focus:ring-blue-500"
          />
          <span className="ml-3 text-gray-900">{option}</span>
        </label>
      ))}
    </div>
  );

  const renderPointBased = () => (
    <div className="space-y-4">
      {question.options.map((option, index) => (
        <label
          key={index}
          className="flex items-center p-4 border rounded-lg cursor-pointer hover:bg-gray-50 transition-colors duration-200"
        >
          <input
            type="radio"
            name={`question-${question.id}`}
            value={index}
            checked={currentAnswer === index}
            onChange={() => onAnswer(question.id, index)}
            className="h-4 w-4 text-blue-600 focus:ring-blue-500"
          />
          <span className="ml-3 text-gray-900">{option}</span>
          <span className="ml-auto text-sm text-gray-500">
            {question.points} points
          </span>
        </label>
      ))}
    </div>
  );

  return (
    <div className="bg-white shadow rounded-lg p-6">
      {timerType === 'per_question' && question.timeLimit > 0 && (
        <div className="mb-4">
          <Timer
            initialTime={question.timeLimit}
            onTimeUp={onTimeUp}
            isActive={true}
          />
        </div>
      )}
      
      <h3 className="text-lg font-medium text-gray-900 mb-4">
        {question.text}
      </h3>
      
      {question.type === ASSESSMENT_TYPES.LIKERT
        ? renderLikertScale()
        : renderPointBased()}
    </div>
  );
};

AssessmentQuestion.propTypes = {
  question: PropTypes.shape({
    id: PropTypes.string.isRequired,
    text: PropTypes.string.isRequired,
    type: PropTypes.string.isRequired,
    options: PropTypes.arrayOf(PropTypes.string).isRequired,
    points: PropTypes.number,
    timeLimit: PropTypes.number
  }).isRequired,
  onAnswer: PropTypes.func.isRequired,
  currentAnswer: PropTypes.number,
  timerType: PropTypes.string,
  onTimeUp: PropTypes.func
};

export default AssessmentQuestion;
