import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import AssessmentDashboard from '../components/AssessmentDashboard';
import AssessmentQuestion from '../components/AssessmentQuestion';
import AssessmentResult from '../components/AssessmentResult';
import Timer from '../components/Timer';
import { ASSESSMENT_TYPES, TIMER_TYPES } from '../utils/types';

const SelfAssessmentPage = () => {
  const { user } = useAuth();
  const [currentView, setCurrentView] = useState('dashboard'); // 'dashboard', 'assessment', 'result'
  const [currentAssessment, setCurrentAssessment] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [assessmentHistory, setAssessmentHistory] = useState([]);
  const [score, setScore] = useState(null);
  const [availableAssessments, setAvailableAssessments] = useState([]);

  // Mock data for demonstration
  useEffect(() => {
    // In a real app, this would fetch from your API
    setAssessmentHistory([
      {
        id: '1',
        title: 'JavaScript Fundamentals',
        score: 85,
        completedAt: '2024-03-15T10:30:00Z'
      },
      {
        id: '2',
        title: 'React Basics',
        score: 92,
        completedAt: '2024-03-14T15:45:00Z'
      }
    ]);

    // Also fetch available assessments
    setAvailableAssessments([
      {
        id: '3',
        title: 'Advanced JavaScript',
        description: 'Test your knowledge of advanced JavaScript concepts',
        questions: [
          { id: 'q1', text: 'What is a closure?', correctAnswer: 0 },
          { id: 'q2', text: 'What is event delegation?', correctAnswer: 0 },
          { id: 'q3', text: 'What is the difference between == and ===?', correctAnswer: 0 }
        ],
        totalTime: 60,
        timerType: TIMER_TYPES.PER_QUESTION
      },
      {
        id: '4',
        title: 'React Testing',
        description: 'Learn how to test React applications',
        questions: [
          { id: 'q1', text: 'What is Jest?', correctAnswer: 0 },
          { id: 'q2', text: 'What is React Testing Library?', correctAnswer: 0 },
          { id: 'q3', text: 'What is the difference between shallow and mount render?', correctAnswer: 0 }
        ],
        totalTime: 90,
        timerType: TIMER_TYPES.FULL_TEST
      }
    ]);
  }, []);

  const handleStartAssessment = (assessment) => {
    setCurrentAssessment(assessment);
    setCurrentQuestionIndex(0);
    setAnswers({});
    setCurrentView('assessment');
  };

  const handleAnswer = (questionId, answerIndex) => {
    setAnswers(prev => ({
      ...prev,
      [questionId]: answerIndex
    }));
  };

  const handleNextQuestion = () => {
    if (currentQuestionIndex < currentAssessment.questions.length - 1) {
      setCurrentQuestionIndex(prev => prev + 1);
    } else {
      handleSubmitAssessment();
    }
  };

  const handlePreviousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(prev => prev - 1);
    }
  };

  const handleSubmitAssessment = () => {
    // Calculate score
    const totalQuestions = currentAssessment.questions.length;
    const correctAnswers = Object.entries(answers).filter(([questionId, answerIndex]) => {
      const question = currentAssessment.questions.find(q => q.id === questionId);
      return question.correctAnswer === answerIndex;
    }).length;

    const calculatedScore = Math.round((correctAnswers / totalQuestions) * 100);
    setScore(calculatedScore);

    // Add to history
    const newHistory = {
      id: currentAssessment.id,
      title: currentAssessment.title,
      score: calculatedScore,
      completedAt: new Date().toISOString()
    };
    setAssessmentHistory(prev => [newHistory, ...prev]);

    setCurrentView('result');
  };

  const handleTimeUp = () => {
    if (currentAssessment.timerType === TIMER_TYPES.PER_QUESTION) {
      handleNextQuestion();
    } else {
      handleSubmitAssessment();
    }
  };

  const renderContent = () => {
    switch (currentView) {
      case 'dashboard':
        return (
          <div className="space-y-6">
            <div className="flex justify-between items-center">
              <h1 className="text-2xl font-bold text-gray-900">Self-Assessments</h1>
              {user?.role === 'admin' && (
                <button
                  onClick={() => setCurrentView('admin')}
                  className="btn-primary"
                >
                  Manage Assessments
                </button>
              )}
            </div>
            <AssessmentDashboard assessmentHistory={assessmentHistory} />

            {/* Available Assessments */}
            <div className="bg-white shadow rounded-lg p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Available Assessments</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {availableAssessments.map((assessment) => (
                  <div key={assessment.id} className="border rounded-lg p-6">
                    <h3 className="text-lg font-medium text-gray-900 mb-2">{assessment.title}</h3>
                    <p className="text-gray-600 mb-4">{assessment.description}</p>
                    <div className="flex flex-wrap gap-2 mb-4">
                      <span className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm">
                        {assessment.questions.length} Questions
                      </span>
                      <span className="px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm">
                        {assessment.totalTime} {assessment.timerType === TIMER_TYPES.PER_QUESTION ? 'min per question' : 'min total'}
                      </span>
                    </div>
                    <button
                      onClick={() => handleStartAssessment(assessment)}
                      className="btn-primary w-full"
                    >
                      Start Assessment
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        );

      case 'assessment':
        const currentQuestion = currentAssessment.questions[currentQuestionIndex];
        return (
          <div className="max-w-3xl mx-auto space-y-6">
            <div className="flex justify-between items-center">
              <h2 className="text-xl font-semibold text-gray-900">
                {currentAssessment.title}
              </h2>
              {currentAssessment.timerType === TIMER_TYPES.FULL_TEST && (
                <Timer
                  initialTime={currentAssessment.totalTime * 60}
                  onTimeUp={handleTimeUp}
                />
              )}
            </div>

            <div className="mb-4">
              <span className="text-sm font-medium text-gray-500">
                Question {currentQuestionIndex + 1} of {currentAssessment.questions.length}
              </span>
            </div>

            <AssessmentQuestion
              question={currentQuestion}
              onAnswer={handleAnswer}
              currentAnswer={answers[currentQuestion.id]}
              timerType={currentAssessment.timerType}
              onTimeUp={handleTimeUp}
            />

            <div className="flex justify-between">
              <button
                onClick={handlePreviousQuestion}
                disabled={currentQuestionIndex === 0}
                className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Previous
              </button>
              <button
                onClick={handleNextQuestion}
                className="btn-primary"
              >
                {currentQuestionIndex === currentAssessment.questions.length - 1
                  ? 'Submit'
                  : 'Next'}
              </button>
            </div>
          </div>
        );

      case 'result':
        return (
          <div className="max-w-3xl mx-auto">
            <AssessmentResult
              assessment={currentAssessment}
              score={score}
              answers={answers}
            />
            <div className="mt-6 flex justify-center">
              <button
                onClick={() => setCurrentView('dashboard')}
                className="btn-primary"
              >
                Back to Dashboard
              </button>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      {renderContent()}
    </div>
  );
};

export default SelfAssessmentPage;
