import React from 'react';
import { Link } from 'react-router-dom';

// Mock data for courses
const courses = [
  {
    id: 1,
    title: 'Introduction to Web Development',
    description: 'Learn the basics of web development including HTML, CSS, and JavaScript.',
    instructor: 'John Smith',
    duration: '8 weeks',
    level: 'Beginner',
    thumbnail: 'https://via.placeholder.com/300x200',
  },
  {
    id: 2,
    title: 'Advanced JavaScript',
    description: 'Master advanced JavaScript concepts and modern frameworks.',
    instructor: 'Sarah Johnson',
    duration: '10 weeks',
    level: 'Advanced',
    thumbnail: 'https://via.placeholder.com/300x200',
  },
  {
    id: 3,
    title: 'React Fundamentals',
    description: 'Learn React.js from scratch and build modern web applications.',
    instructor: 'Mike Brown',
    duration: '6 weeks',
    level: 'Intermediate',
    thumbnail: 'https://via.placeholder.com/300x200',
  },
];

const Courses = () => {
  return (
    <div className="space-y-8">
      <div className="flex items-center justify-between">
        <h1 className="text-2xl font-bold text-gray-900">Available Courses</h1>
        <div className="flex space-x-4">
          <select className="input-field max-w-xs">
            <option value="">All Levels</option>
            <option value="beginner">Beginner</option>
            <option value="intermediate">Intermediate</option>
            <option value="advanced">Advanced</option>
          </select>
          <input
            type="text"
            placeholder="Search courses..."
            className="input-field max-w-xs"
          />
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {courses.map((course) => (
          <div
            key={course.id}
            className="bg-white rounded-lg shadow overflow-hidden"
          >
            <img
              src={course.thumbnail}
              alt={course.title}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h3 className="text-lg font-medium text-gray-900">
                {course.title}
              </h3>
              <p className="mt-1 text-sm text-gray-500">
                Instructor: {course.instructor}
              </p>
              <p className="mt-2 text-sm text-gray-600">
                {course.description}
              </p>
              <div className="mt-4 flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  <span className="px-2 py-1 text-xs font-medium bg-blue-100 text-blue-800 rounded-full">
                    {course.level}
                  </span>
                  <span className="text-sm text-gray-500">
                    {course.duration}
                  </span>
                </div>
                <Link
                  to={`/courses/${course.id}`}
                  className="text-sm font-medium text-blue-600 hover:text-blue-500"
                >
                  View Course
                </Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Courses;
