import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import logo from './logo.jpg';

const Navbar = () => {
  const { isAuthenticated, logout, user } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <nav className="bg-primary-background shadow-lg font-['Montserrat']">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-20 items-center">
          <div className="flex items-center">
            <Link to="/" className="flex-shrink-0 flex items-center">
              <img
                className="h-10 w-auto"
                src={logo}
                alt="The Catalystz"
              />
            </Link>
          </div>

          <div className="flex items-center">
            {isAuthenticated ? (
              <div className="flex items-center space-x-6">
                {user?.role === 'admin' && (
                  <Link
                    to="/admin"
                    className="text-primary-text hover:text-[#5C6BC0] px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200 flex items-center"
                  >
                     <i className="fas fa-cogs mr-1"></i> Admin
                  </Link>
                )}
                 <Link
                    to="/student/dashboard"
                    className="text-primary-text hover:text-[#5C6BC0] px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200 flex items-center"
                  >
                    <i className="fas fa-tachometer-alt mr-1"></i> Dashboard
                  </Link>
                   <Link
                    to="/student/assessments"
                    className="text-primary-text hover:text-[#5C6BC0] px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200 flex items-center"
                  >
                    <i className="fas fa-clipboard-list mr-1"></i> Assessments
                  </Link>
                <div className="flex items-center justify-center h-10 w-10 rounded-full bg-primary-text text-primary-background font-bold text-lg">
                  {user?.name ? user.name.charAt(0).toUpperCase() : 'D'}
                </div>
              </div>
            ) : (
              <div className="flex items-center space-x-4">
                <Link
                  to="/login"
                  className="text-primary-text hover:text-[#5C6BC0] px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200"
                >
                  Login
                </Link>
                <Link
                  to="/register"
                  className="btn-primary text-sm font-medium"
                >
                  Register
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
