import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { ASSESSMENT_TYPES, TIMER_TYPES, createAssessment, createQuestion } from '../utils/types';
import { toast } from 'react-hot-toast';

const AdminAssessmentManager = ({ assessments, onSave, onDelete }) => {
  const [selectedAssessment, setSelectedAssessment] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    type: ASSESSMENT_TYPES.LIKERT,
    timerType: TIMER_TYPES.NONE,
    totalTime: 0,
    passingScore: 70,
    questions: []
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleQuestionChange = (index, field, value) => {
    setFormData((prev) => {
      const newQuestions = [...prev.questions];
      newQuestions[index] = {
        ...newQuestions[index],
        [field]: value
      };
      return { ...prev, questions: newQuestions };
    });
  };

  const addQuestion = () => {
    setFormData((prev) => ({
      ...prev,
      questions: [
        ...prev.questions,
        createQuestion({
          id: Date.now().toString(),
          text: '',
          type: prev.type,
          options: ['Option 1', 'Option 2'] // Start with two generic options
        })
      ]
    }));
  };

  const removeQuestion = (index) => {
    setFormData((prev) => ({
      ...prev,
      questions: prev.questions.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const assessment = createAssessment({
      ...formData,
      id: selectedAssessment?.id || Date.now().toString()
    });
    onSave(assessment);
    resetForm();
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      type: ASSESSMENT_TYPES.LIKERT,
      timerType: TIMER_TYPES.NONE,
      totalTime: 0,
      passingScore: 70,
      questions: []
    });
    setSelectedAssessment(null);
    setIsEditing(false);
  };

  const editAssessment = (assessment) => {
    setSelectedAssessment(assessment);
    setFormData(assessment);
    setIsEditing(true);
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();

      reader.onload = (event) => {
        try {
          const fileContent = event.target.result;
          const fileExtension = file.name.split('.').pop().toLowerCase();

          if (fileExtension === 'json') {
            const parsedData = JSON.parse(fileContent);
            if (parsedData.title && parsedData.description && Array.isArray(parsedData.questions)) {
              setFormData(prev => ({
                ...prev,
                questions: [...prev.questions, ...parsedData.questions.map(q => createQuestion(q))]
              }));
              setIsEditing(true);
              setSelectedAssessment(null);
              toast.success(`Assessment questions loaded from JSON file! Added ${parsedData.questions.length} questions.`);
            } else {
              toast.error('Invalid JSON format in file for assessment. Missing title, description, or questions array.');
            }
          } else if (fileExtension === 'csv') {
            const lines = fileContent.split(/\r?\n/).filter(line => line.trim() !== '');
            if (lines.length > 1) { // Expect at least header and one data row
              const headers = lines[0].split(',').map(h => h.trim());
              const csvQuestions = [];

              for (let i = 1; i < lines.length; i++) {
                const values = lines[i].split(',').map(v => v.trim());
                const questionData = {};
                headers.forEach((header, index) => {
                  questionData[header] = values[index];
                });

                const options = [];
                for (let j = 1; ; j++) {
                  const optionKey = `Option${j}`;
                  if (questionData[optionKey]) {
                    options.push(questionData[optionKey]);
                  } else {
                    break;
                  }
                }

                if (questionData.QuestionText && options.length > 0 && questionData.CorrectAnswerIndex !== undefined) {
                  csvQuestions.push(createQuestion({
                    id: `csv-q-${i}-${Date.now()}`,
                    text: questionData.QuestionText,
                    type: questionData.Type || ASSESSMENT_TYPES.POINT_BASED,
                    options: options,
                    points: parseInt(questionData.Points) || 1,
                    timeLimit: parseInt(questionData.TimeLimit) || 0,
                    correctAnswer: parseInt(questionData.CorrectAnswerIndex)
                  }));
                }
              }

              if (csvQuestions.length > 0) {
                setFormData(prev => ({
                  ...prev,
                  questions: [...prev.questions, ...csvQuestions]
                }));
                setIsEditing(true);
                setSelectedAssessment(null);
                toast.success(`Assessment questions loaded from CSV! Added ${csvQuestions.length} questions.`);
              } else {
                toast.error('No valid questions found in CSV file. Please check format and ensure all required columns are present.');
              }
            } else {
              toast.error('CSV file is empty or malformed (requires header and at least one question row).');
            }
          } else if (fileExtension === 'docx') {
            toast.error('Word (.docx) structured content parsing is not supported on the frontend. A backend server is required for this functionality.');
            // Optionally, set basic info from filename - will not add questions
            setFormData(prev => ({
              ...prev,
              title: file.name.replace('.docx', '') || prev.title,
              description: 'Questions cannot be extracted from DOCX on frontend.',
              // Keep existing questions, do not overwrite
            }));
            setIsEditing(true);
            setSelectedAssessment(null);
          } else {
            toast.error('Unsupported file type. Please upload a JSON, CSV, or DOCX file.');
          }
        } catch (error) {
          toast.error('Error processing file: ' + error.message);
        }
      };
      reader.readAsText(file);
    } else {
      toast.error('No file selected.');
    }
  };

  return (
    <div className="space-y-6">
      {/* Assessment List */}
      <div className="bg-white shadow rounded-lg p-6">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-xl font-semibold text-gray-900">Assessments</h2>
          <button
            onClick={() => {
              resetForm();
              setIsEditing(true);
            }}
            className="btn-primary"
            type="button"
          >
            Create New Assessment
          </button>
        </div>
        <div className="space-y-4">
          {assessments.map((assessment) => (
            <div
              key={assessment.id}
              className="flex items-center justify-between p-4 border rounded-lg"
            >
              <div>
                <h3 className="font-medium text-gray-900">{assessment.title}</h3>
                <p className="text-sm text-gray-600">{assessment.description}</p>
              </div>
              <div className="flex items-center space-x-4">
                <button
                  onClick={() => editAssessment(assessment)}
                  className="text-blue-600 hover:text-blue-500"
                >
                  Edit
                </button>
                <button
                  onClick={() => onDelete(assessment.id)}
                  className="text-red-600 hover:text-500"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Assessment Form */}
      {isEditing && (
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">
            {selectedAssessment ? 'Edit Assessment' : 'Create Assessment'}
          </h2>

          {/* Universal File Upload Section */}
          <div className="mb-6 p-4 border rounded-lg bg-gray-50">
            <h3 className="text-lg font-medium text-gray-900 mb-2">Upload Assessment from File</h3>
            <input
              type="file"
              accept=".json,.csv,.docx"
              onChange={handleFileSelect}
              className="mt-1 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-primary-dark-blue file:text-white hover:file:bg-primary-text hover:file:cursor-pointer"
            />
            <p className="mt-2 text-sm text-gray-600">
              Upload a JSON, CSV, or Word (.docx) file.
              For CSV, ensure a strict format: `QuestionText,Option1,Option2,...,CorrectAnswerIndex,Type(optional),Points(optional),TimeLimit(optional)`.
              <strong className="text-red-600"> DOCX structured content parsing is not supported on frontend; backend is required.</strong>
            </p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700">Title</label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">Description</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                rows={3}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                required
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700">Type</label>
                <select
                  name="type"
                  value={formData.type}
                  onChange={handleInputChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                >
                  <option value={ASSESSMENT_TYPES.LIKERT}>Likert Scale</option>
                  <option value={ASSESSMENT_TYPES.POINT_BASED}>Point Based</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700">Timer Type</label>
                <select
                  name="timerType"
                  value={formData.timerType}
                  onChange={handleInputChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                >
                  <option value={TIMER_TYPES.NONE}>No Timer</option>
                  <option value={TIMER_TYPES.PER_QUESTION}>Per Question</option>
                  <option value={TIMER_TYPES.FULL_TEST}>Full Test</option>
                </select>
              </div>
            </div>

            {formData.timerType !== TIMER_TYPES.NONE && (
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  {formData.timerType === TIMER_TYPES.FULL_TEST
                    ? 'Total Time (minutes)'
                    : 'Time per Question (seconds)'}
                </label>
                <input
                  type="number"
                  name="totalTime"
                  value={formData.totalTime}
                  onChange={handleInputChange}
                  min="1"
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                  required
                />
              </div>
            )}

            <div>
              <label className="block text-sm font-medium text-gray-700">Passing Score (%)</label>
              <input
                type="number"
                name="passingScore"
                value={formData.passingScore}
                onChange={handleInputChange}
                min="0"
                max="100"
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                required
              />
            </div>

            {/* Questions Section */}
            <div>
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-medium text-gray-900">Questions</h3>
                <button
                  type="button"
                  onClick={addQuestion}
                  className="text-blue-600 hover:text-blue-500"
                >
                  Add Question
                </button>
              </div>
              <div className="space-y-4">
                {formData.questions.map((question, index) => (
                  <div key={question.id} className="border rounded-lg p-4">
                    <div className="flex items-center justify-between mb-4">
                      <h4 className="font-medium text-gray-900">Question {index + 1}</h4>
                      <button
                        type="button"
                        onClick={() => removeQuestion(index)}
                        className="text-red-600 hover:text-red-500"
                      >
                        Remove
                      </button>
                    </div>
                    <div className="space-y-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700">
                          Question Text
                        </label>
                        <input
                          type="text"
                          value={question.text}
                          onChange={(e) =>
                            handleQuestionChange(index, 'text', e.target.value)
                          }
                          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                          required
                        />
                      </div>
                      {formData.type === ASSESSMENT_TYPES.POINT_BASED && (
                        <div>
                          <label className="block text-sm font-medium text-gray-700">
                            Points
                          </label>
                          <input
                            type="number"
                            value={question.points}
                            onChange={(e) =>
                              handleQuestionChange(index, 'points', parseInt(e.target.value))
                            }
                            min="1"
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                            required
                          />
                        </div>
                      )}
                      <div>
                        <label className="block text-sm font-medium text-gray-700">
                          Options
                        </label>
                        {question.options.map((option, optionIndex) => (
                          <div key={optionIndex} className="flex items-center mt-1">
                            <input
                              type="text"
                              value={option}
                              onChange={(e) => {
                                const newOptions = [...question.options];
                                newOptions[optionIndex] = e.target.value;
                                handleQuestionChange(index, 'options', newOptions);
                              }}
                              className="block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                              required
                            />
                            {question.options.length > 2 && ( // Allow removing only if more than 2 options
                              <button
                                type="button"
                                onClick={() => {
                                  const newOptions = question.options.filter((_, i) => i !== optionIndex);
                                  handleQuestionChange(index, 'options', newOptions);
                                }}
                                className="ml-2 text-red-600 hover:text-red-500 text-sm"
                              >
                                Remove
                              </button>
                            )}
                          </div>
                        ))}
                        <button
                          type="button"
                          onClick={() => {
                            const newOptions = [...question.options, `Option ${question.options.length + 1}`];
                            handleQuestionChange(index, 'options', newOptions);
                          }}
                          className="mt-2 text-blue-600 hover:text-blue-500 text-sm"
                        >
                          Add Option
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <div className="flex justify-end space-x-4">
              <button
                type="button"
                onClick={resetForm}
                className="btn-secondary"
              >
                Cancel
              </button>
              <button type="submit" className="btn-primary">
                {selectedAssessment ? 'Update Assessment' : 'Create Assessment'}
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};

AdminAssessmentManager.propTypes = {
  assessments: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      title: PropTypes.string.isRequired,
      description: PropTypes.string.isRequired,
      type: PropTypes.string.isRequired,
      questions: PropTypes.array.isRequired,
      timerType: PropTypes.string.isRequired,
      totalTime: PropTypes.number.isRequired,
      passingScore: PropTypes.number.isRequired
    })
  ).isRequired,
  onSave: PropTypes.func.isRequired,
  onDelete: PropTypes.func.isRequired
};

export default AdminAssessmentManager;
