import React from 'react';
import PropTypes from 'prop-types';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line, Bar, Pie } from 'react-chartjs-2';
import { ASSESSMENT_TYPES, SCORE_RANGES } from '../utils/types';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

// Wrapper components for each chart type
const LineChartWrapper = ({ data, options }) => (
  <div style={{ height: '300px', position: 'relative' }}>
    <Line data={data} options={options} />
  </div>
);

const BarChartWrapper = ({ data, options }) => (
  <div style={{ height: '300px', position: 'relative' }}>
    <Bar data={data} options={options} />
  </div>
);

const PieChartWrapper = ({ data, options }) => (
  <div style={{ height: '300px', position: 'relative' }}>
    <Pie data={data} options={options} />
  </div>
);

const AssessmentVisualizations = ({ history, assessments }) => {
  // Prepare data for score trends
  const scoreTrendsData = {
    labels: history
      .sort((a, b) => new Date(a.completedAt) - new Date(b.completedAt))
      .map((item) => new Date(item.completedAt).toLocaleDateString()),
    datasets: [
      {
        label: 'Score',
        data: history
          .sort((a, b) => new Date(a.completedAt) - new Date(b.completedAt))
          .map((item) => item.score),
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
      },
    ],
  };

  // Prepare data for section-wise performance
  const sectionPerformanceData = {
    labels: Object.keys(ASSESSMENT_TYPES),
    datasets: [
      {
        label: 'Average Questions',
        data: Object.keys(ASSESSMENT_TYPES).map((type) => {
          const typeAssessments = assessments.filter((a) => a.type === type);
          return typeAssessments.length > 0
            ? typeAssessments.reduce((acc, curr) => acc + curr.questions.length, 0) / typeAssessments.length
            : 0;
        }),
        backgroundColor: 'rgba(54, 162, 235, 0.5)',
      },
    ],
  };

  // Prepare data for score distribution
  const scoreDistributionData = {
    labels: Object.values(SCORE_RANGES).map((range) => range.label),
    datasets: [
      {
        data: Object.values(SCORE_RANGES).map((range) =>
          history.filter((item) => item.score >= range.min && item.score <= range.max).length
        ),
        backgroundColor: Object.values(SCORE_RANGES).map((range) => {
          switch (range.color) {
            case 'green':
              return 'rgba(75, 192, 192, 0.5)';
            case 'blue':
              return 'rgba(54, 162, 235, 0.5)';
            case 'yellow':
              return 'rgba(255, 205, 86, 0.5)';
            case 'red':
              return 'rgba(255, 99, 132, 0.5)';
            default:
              return 'rgba(201, 203, 207, 0.5)';
          }
        }),
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Assessment Performance',
      },
    },
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      <div className="bg-white shadow rounded-lg p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Score Trends</h3>
        <LineChartWrapper data={scoreTrendsData} options={chartOptions} />
      </div>

      <div className="bg-white shadow rounded-lg p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Section-wise Performance</h3>
        <BarChartWrapper data={sectionPerformanceData} options={chartOptions} />
      </div>

      <div className="bg-white shadow rounded-lg p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Score Distribution</h3>
        <PieChartWrapper data={scoreDistributionData} options={chartOptions} />
      </div>
    </div>
  );
};

// PropTypes for wrapper components
LineChartWrapper.propTypes = {
  data: PropTypes.object.isRequired,
  options: PropTypes.object.isRequired,
};

BarChartWrapper.propTypes = {
  data: PropTypes.object.isRequired,
  options: PropTypes.object.isRequired,
};

PieChartWrapper.propTypes = {
  data: PropTypes.object.isRequired,
  options: PropTypes.object.isRequired,
};

AssessmentVisualizations.propTypes = {
  history: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      assessmentId: PropTypes.string.isRequired,
      score: PropTypes.number.isRequired,
      completedAt: PropTypes.string.isRequired,
    })
  ).isRequired,
  assessments: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      title: PropTypes.string.isRequired,
      type: PropTypes.string.isRequired,
      questions: PropTypes.array.isRequired,
    })
  ).isRequired,
};

export default AssessmentVisualizations;
